QueryDef.SQL:
PARAMETERS iOrderType Long, dtMinRequestDate DateTime;
SELECT OrderHeader.iOrderID, OrderHeader.vchOrderNumber AS [Order Number], Customer.vchCustomerName AS Customer, OrderHeader.vchJobName AS Jobname, OrderHeader.vchPONumber AS PO, OrderHeader.dtRequestDate AS ReqDate, DocumentStatus.vchStatusDescription AS OrderStatus, [vchFirstName] & " " & [vchLastName] AS [OE Rep], POStatus.StatusText AS POStatus, OrderHeader.tiPOStatusID, OrderHeader.tiRush
FROM (((OrderHeader INNER JOIN Customer ON OrderHeader.iCustomerID = Customer.iCustomerID) INNER JOIN OrderRep ON OrderHeader.iUserID = OrderRep.iRepID) INNER JOIN DocumentStatus ON OrderHeader.iStatus = DocumentStatus.iDocumentStatus) INNER JOIN POStatus ON OrderHeader.tiPOStatusID = POStatus.tiPOStatusID
WHERE (((OrderHeader.dtRequestDate)>[dtMinRequestDate]) AND ((OrderHeader.iOrderType)=[iOrderType]) AND ((OrderHeader.iStatus)=0 Or (OrderHeader.iStatus)=1));

PARAMETERS iPurchaseOrderLineID Long;
SELECT PurchaseOrderDetailMapping.iPurchaseOrderLineID, OrderLine.iOrdered, OrderLine.flSlabLength, OrderLine.txtLineDesc, [Customer].[vchExternalID] & ":" & [OrderHeader].[vchPONumber] & ":" & [OrderHeader].[vchOrderNumber] AS vchInfo, OrderLine.iLineNumber, OrderLine.iSerialID, OrderLine.iOrderID
FROM (OrderHeader INNER JOIN Customer ON OrderHeader.iCustomerID = Customer.iCustomerID) INNER JOIN (PurchaseOrderDetailMapping INNER JOIN OrderLine ON PurchaseOrderDetailMapping.iSerialID = OrderLine.iSerialID) ON OrderHeader.iOrderID = OrderLine.iOrderID
WHERE (((PurchaseOrderDetailMapping.iPurchaseOrderLineID)=[iPurchaseOrderLineID]));

PARAMETERS iPurchaseOrderID Long;
SELECT DISTINCT OrderLine.iOrderID
FROM OrderLine INNER JOIN (PurchaseOrderDetail INNER JOIN PurchaseOrderDetailMapping ON PurchaseOrderDetail.iPurchaseOrderLineID = PurchaseOrderDetailMapping.iPurchaseOrderLineID) ON (OrderLine.iSerialID = PurchaseOrderDetailMapping.iSerialID) AND (OrderLine.iSerialID = PurchaseOrderDetailMapping.iSerialID)
WHERE (((PurchaseOrderDetail.iPurchaseOrderID)=[iPurchaseOrderID]));

PARAMETERS dtOrderDate DateTime;
SELECT OrderHeader.iOrderID, Sum(PurchaseOrderDetail.iQuantity*PurchaseOrderDetail.flSize) AS TotalFootage, First(PurchaseOrderDetail.vchGroupByCode) AS vchGroupByCode
FROM PurchaseOrderDetail, OrderHeader
WHERE (((OrderHeader.dtRequestDate)<[dtOrderDate]) AND ((OrderHeader.iOrderID)=0-[PurchaseOrderDetail].[iPurchaseOrderID]) AND ((OrderHeader.iStatus)=0))
GROUP BY OrderHeader.iOrderID;

PARAMETERS iOrderID Long;
SELECT IIf([tiSpecialOrder],[qkpovOrderItemsOnOrder.vchBrandCode],"Z") AS vchBrandCode, qkpovOrderItemsOnOrder.vchLaminateCode, qkpovOrderItemsOnOrder.vchVendorCode, qkpovOrderItemsOnOrder.vchVendorDesc, qkpovOrderItemsOnOrder.iSlabID, qkpovOrderItemsOnOrder.iSerialID, qkpovOrderItemsOnOrder.txtLineDesc, qkpovOrderItemsOnOrder.iLineNumber, qkpovOrderItemsOnOrder.flOrderableLength, qkpovOrderItemsOnOrder.iOrdered, qkpovOrderItemsOnOrder.iVendorSlabTypeID, qkpovOrderItemsOnOrder.iVendorID, qkpovOrderItemsOnOrder.tiSpecialOrder
FROM qkpovOrderItemsOnOrder
ORDER BY IIf([tiSpecialOrder],[qkpovOrderItemsOnOrder.vchBrandCode],"Z"), qkpovOrderItemsOnOrder.vchLaminateCode, qkpovOrderItemsOnOrder.vchVendorCode;

PARAMETERS iPOStatus Long;
SELECT PurchaseOrder.iPurchaseOrderID, PurchaseOrder.vchPONumber, PurchaseOrder.dtRequested, PurchaseOrder.dtDateOrdered, PurchaseOrder.dtReceived, PurchaseOrder.iPOStatus
FROM PurchaseOrder
WHERE (((PurchaseOrder.iPOStatus)=[iPOStatus]));

PARAMETERS iOrderID Long;
SELECT qkpovPOItems.vchBrandCode, qkpovPOItems.vchLaminateCode, qkpovPOItems.vchVendorCode, qkpovPOItems.vchVendorDesc, qkpovPOItems.flOrderableLength, qkpovPOItems.iSlabID, qkpovPOItems.iSerialID, qkpovPOItems.txtLineDesc, qkpovPOItems.iLineNumber, qkpovPOItems.iOrdered, qkpovPOItems.iVendorSlabTypeID, qkpovPOItems.iVendorID, qkpovPOItems.tiSpecialOrder, qkpovPOItems.iPricecode, qkpovPOItems.iOrderID, qkpovPOItems.tiOnPo
FROM qkpovPOItems
WHERE (((qkpovPOItems.iOrderID)=[iOrderID]) AND ((qkpovPOItems.tiOnPo)=False));

PARAMETERS iPurchaseOrderID Long;
SELECT qkpovPurchaseOrderSlabTypes.vchGroupByCode AS vchLaminateCode, qkpovPurchaseOrderSlabTypes.vchVendorItemCode AS vchVendorCode, qkpovPurchaseOrderSlabTypes.flSize, 1 AS iOrdered, "Z" AS vchBrandCode, qkpovPurchaseOrderSlabTypes.vchItemDescription AS vchVendorDesc, qkpovPurchaseOrderSlabTypes.iVendorSlabTypeID, qkpovPurchaseOrderSlabTypes.iPurchaseOrderLineID AS iSerialID, qkpovPurchaseOrderSlabTypes.dcLengthUsed
FROM qkpovPurchaseOrderSlabTypes
WHERE (((qkpovPurchaseOrderSlabTypes.flSize)>0) AND ((qkpovPurchaseOrderSlabTypes.iPurchaseOrderID)=[iPurchaseOrderID]) AND ((Len([vchGroupByCode]))>0) AND ((Len([vchVendorItemCode]))>0))
ORDER BY qkpovPurchaseOrderSlabTypes.vchGroupByCode, qkpovPurchaseOrderSlabTypes.vchVendorItemCode;

PARAMETERS iPurchaseOrderID Long;
SELECT PurchaseOrderDetail.iPurchaseOrderID, PurchaseOrderDetail.iPurchaseOrderLineID, PurchaseOrderDetail.vchGroupByCode, PurchaseOrderDetail.vchVendorItemCode, PurchaseOrderDetail.vchItemDescription, PurchaseOrderDetail.flSize, PurchaseOrderDetail.iQuantity, PurchaseOrderDetail.tiIsSlab, qkpovSlabVendorTypes.iVendorSlabTypeID, PurchaseOrderDetail.dcLengthUsed
FROM PurchaseOrderDetail INNER JOIN qkpovSlabVendorTypes ON PurchaseOrderDetail.vchVendorItemCode = qkpovSlabVendorTypes.vchVendorCode
WHERE (((PurchaseOrderDetail.iPurchaseOrderID)=[iPurchaseOrderID]));
